$(function(){function backToTop(){label="Back to top",container_selector="body",display_at=400,$bt=null,$bt_container=$(container_selector);var bt_html='<a id="back-top-button" class="backtotop-button">'+label+"</a>";$bt=$(bt_html).prependTo($bt_container),$(window).scroll(function(){$(this).scrollTop()>display_at?"none"==$bt.css("display")&&$bt.fadeIn(500):$bt.fadeOut(300)}),$bt.click(function(event){event.preventDefault(),$("html, body").animate({scrollTop:0},300)}),$(".form-text, textarea").blur(function(){$(".backtotop-button").removeClass("element-hidden")}).focus(function(){$(".backtotop-button").addClass("element-hidden")})}backToTop()});